mapper.select:
        select ic.id_integrante_comision, ic.id_comision, ic.nombre
        from 
            dea.integrantes_comision ic

        where ic.id_comision in (
            select c.id_comision from
            dea.comision c            
            where 
				c.id_facultad = #{id_facultad}
				and c.id_unidad = #{id_unidad}
        )
