mapper.select:
select * From dea.planilla_avance_detalle p
	where p.id_planilla_avance_detalle = #{id}
order by p.id_planilla_avance_detalle
;

mapper.insert:
INSERT INTO dea.planilla_avance_detalle
(fecha_planificacion, fecha_ejecucion, actividad, observacion, id_estado, id_usuario_mod, fecha_mod, id_tipo_actividad, id_planilla_avance)
VALUES(#{fecha_planificacion}, #{fecha_ejecucion}, #{actividad}, #{observacion}, #{id_estado}, #{id_usuario_mod}, #{fecha_mod}, #{id_tipo_actividad}, #{id_planilla_avance});
